mapper.update:
update

set
FILENAME = #{FILENAME},
FILE_URL = #{newUrl}
where
FILE_URL = #{FILE_URL}

mapper.select:
SELECT * FROM TB_FILEMEANS WHERE FILEMEANS_ID IN(
SELECT FILEMEANS_ID FROM TB_FILEMEANS WHERE FILE_CREATEUSER <>  #{USERNAME}
AND FILE_ISENCTYPT = 0 AND FILE_READUSERS = 'ALL(所有人可见)'
UNION ALL
SELECT FILEMEANS_ID FROM TB_FILEMEANS WHERE FILE_CREATEUSER <>  #{USERNAME}
AND FILE_ISENCTYPT = 0 AND FILE_READUSERS LIKE '%'+ #{FILE_READUSERS} +'%'
UNION ALL
SELECT FILEMEANS_ID FROM TB_FILEMEANS WHERE FILE_CREATEUSER <>  #{USERNAME}
AND FILE_ISENCTYPT = 1 AND FILE_PASSUSER LIKE '%'+ #{FILE_READUSERS} +'%')
AND FILE_CATALOGURL_ID = #{FILE_CATALOGURL_ID}

    and
    FILENAME LIKE '%' + #{keywords} + '%'

ORDER BY FILE_TYPE desc, FILENAME asc

SELECT * FROM TB_FILEMEANS WHERE FILE_CREATEUSER = #{USERNAME} AND FILE_CATALOGURL_ID = #{FILE_CATALOGURL_ID}
and FILENAME LIKE '%' + #{keywords} + '%'
ORDER BY FILE_TYPE desc, FILENAME asc

SELECT * FROM TB_FILEMEANS WHERE FILEMEANS_ID IN (select FILEMEANS_ID FROM TB_FILEMEANS WHERE FILE_READUSERS LIKE '%ALL(所有人可见)%' OR FILE_READUSERS LIKE '%'+ #{FILE_READUSERS} +'%') AND FILEMEANS_ID NOT IN( SELECT FILEMEANS_ID FROM TB_FILEMEANS WHERE FILE_PASSUSER LIKE '%'+ #{FILE_READUSERS} +'%') AND FILE_CREATEUSER <> #{USERNAME} AND FILE_ISENCTYPT = 1 AND FILE_CATALOGURL_ID = #{FILE_CATALOGURL_ID}
and FILENAME LIKE '%' + #{keywords} + '%'
ORDER BY FILE_TYPE desc, FILENAME asc
